mapper.update:
UPDATE campaigns
user_seq = #{userSeq}, title = #{title}, contents = #{contents}, service = #{service}, keyword1 = #{keyword1}, keyword2 = #{keyword2}, keyword3 = #{keyword3}, campaign_link = #{campaignLink}, mission = #{mission}, recruiter = #{recruiter}, image = #{image}, category_seq = #{categorySeq}, platform_seq = #{platformSeq}, type_seq = #{typeSeq}, `status` = #{status}, application_start_date = #{applicationStartDate}, application_end_date = #{applicationEndDate}, experience_start_date = #{experienceStartDate}, experience_end_date = #{experienceEndDate}, experience_start_time = #{experienceStartTime}, experience_end_time = #{experienceEndTime}, monday = #{monday}, tuesday = #{tuesday}, wednesday = #{wednesday}, thursday = #{thursday}, friday = #{friday}, saturday = #{saturday}, sunday = #{sunday}, city = #{city}, district = #{district}, `point` = #{point}, location = #{location},
WHERE seq = #{campaignSeq}

UPDATE missions
title = #{title}, hash_tag = #{hashTag}, map = #{map}, link = #{link}, characters = #{characters}, video_length = #{videoLength}, picture_count = #{pictureCount}, account_tag = #{accountTag}, sound = #{sound}, advertising_display = #{advertisingDisplay},
WHERE seq = #{seq}

UPDATE campaign_comments

    comment = #{comment}, 

WHERE seq = #{seq} AND user_seq = #{userSeq}

mapper.select:
SELECT
    COUNT(p.seq)
FROM campaigns p


        AND (p.city, p.district) IN

            #{location.city}, #{location.district}


    AND p.title LIKE #{searchWord}
    AND p.user_seq = #{userSeq}


            AND p.tag != null AND CURDATE() BETWEEN p.application_start_date AND p.application_end_date


            AND CURDATE() BETWEEN p.application_start_date AND p.application_end_date
