mapper.update:
UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq}

UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq} AND user_seq = #{userSeq}

mapper.select:
SELECT p.seq, p.title, p.contents, p.create_date as createDate, p.view_count as viewCount FROM posts p
AND p.title LIKE #{searchWord}
LIMIT #{page}, 10

SELECT COUNT(p.seq)
FROM posts p
LEFT JOIN post_comments comment on p.seq = comment.post_seq

    AND p.type_seq = #{type} /* {typeSeq ë¥¼ type}  */
    AND p.title LIKE #{searchWord}

         AND p.user_seq = #{userSeq} 
         AND `comment`.user_seq = #{userSeq} 


GROUP BY (p.seq)

SELECT COUNT(*)
FROM communities c
JOIN community_option co ON c.option_seq = co.seq AND co.type = #{type}
JOIN user_community uc ON c.user_seq = uc.seq


        AND c.title LIKE #{searchWord} 
        AND uc.name LIKE #{searchWord} 
        AND c.contents LIKE #{searchWord} 

            AND (c.title LIKE #{searchWord} OR uc.name LIKE #{searchWord} OR c.contents LIKE #{searchWord})
