mapper.update:
UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq}

UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq} AND user_seq = #{userSeq}

mapper.select:
SELECT category.category, p.title, u_i.nickname, p.create_date as createDate, p.view_count as viewCount FROM posts p LEFT JOIN users u ON p.user_seq = u.seq LEFT JOIN user_influencer u_i ON u.seq = u_i.user_seq LEFT JOIN post_categories category on p.category_seq = category.seq LEFT JOIN post_comments comment on p.seq = comment.post_seq
AND p.type_seq = #{type} AND p.title LIKE #{searchWord}
AND p.user_seq = #{userSeq} AND comment.user_seq = #{userSeq}
GROUP BY p.seq LIMIT #{page}, 10

SELECT COUNT(p.seq)
FROM posts p
LEFT JOIN post_comments comment on p.seq = comment.post_seq

    AND p.type_seq = #{type} /* {typeSeq ë¥¼ type}  */
    AND p.title LIKE #{searchWord}

         AND p.user_seq = #{userSeq} 
         AND `comment`.user_seq = #{userSeq} 


GROUP BY (p.seq)
