mapper.update:
UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq}

UPDATE posts
category_seq = #{categorySeq}, title = #{title}, contents = #{contents},
WHERE seq = #{seq} AND user_seq = #{userSeq}

mapper.select:
SELECT COUNT(p.seq)
FROM posts p
WHERE p.type_seq = #{typeSeq}
